import React from "react"
import { Component } from 'react';
import {connect} from 'react-redux';
import {filterJobs, fetchJobs, fetchJobDetails} from
  '../actions/searchActions';

class SearchInputForms extends Component {
  onChangejobs = e => {
    this.props.jobs(e.target.value);
  };
  onChangeLocation = e => {
    this.props.location(e.target.value);
  };

  onChangefullTime = e => {
    this.props.fullTime(e.target.checked);
  };

  onChangeDescription = e => {
    this.props.description(e.target.value);
  };

  onChangeDescription = e => {
    this.props.fullTime(e.target.value);
  };

  onChangeJob = e => {
    this.props.job(e.target.value);
  };

  onSubmit = e => {
    e.preventDefault();
    this.props.fetchJobs (this.props.jobs, this.props.job, this.props.description, this.props.location);
  };

  render() {
    return (
      <div className="catagory_area">
        <div className="flex container align-items-center">
            <form id="searchForm" onSubmit={this.onSubmit}>
              <div className="row cat_search m-0 p-0">
                <div className="col-lg-3 col-md-4">

                  <input
                    type="text"
                    className="form-control"
                    name="keywordText"
                    placeholder="Search Keyword"
                    onChange={this.onChangeJob}
                  />
                </div>
                <div className="col-lg-3 col-md-4 flex">
                  <input
                    type="text"
                    className="form-control"
                    name="locationText"
                    placeholder="Location"  
                    onChange={this.onChangeLocation}
                  />
                </div>
                <div className="col-lg-3 col-md-4">
                  <div className="inline-field d-flex justify-content-center">
                    <label className="align-content-center m-0 p-0" htmlFor="contractText">
                    <input
                      id="chkbx"
                      type="checkbox"
                      className="inline-field align-middle"
                      name="contractText"
                      onChange={this.onChangeDescription}
                    />
                      <span className="d-inline flex align-middle pl-sm-3">Full Time</span>
                    </label>
                  </div>
                </div>
                <div className="col-lg-3 col-md-12">
                  <button type="submit" className="boxed-btn3">
                    Find Job
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    location: state.jobs.location,
    description: state.jobs.description,
    fulltime: state.jobs.fulltime,
  }
}

const mapDispatchToProps = {
  fetchJobs
}


export default connect(mapStateToProps, mapDispatchToProps)(SearchInputForms);
