import {
    FILTERJOBS,
    FETCHJOBS,
    FETCHJOBDETAILS,
    LOADING
  } from '../actions/actionTypes';

  const initialState = {

    jobs: [],
    loading: false,
    location: "",
    description: "",
    fullTime: "",
    job: []
  };

  export default function(state = initialState, action) {
    switch (action.type) {
      case FILTERJOBS:
        return {
          ...state,
          text: action.payload,
          description: action.description,
          location:action.location,
          fullTime:action.fullTime

        };
      case FETCHJJOBS:
        return {
          ...state,
          jobs: action.payload,
          loading: false
        };
      case FETCHJOBDETAILS:
        return {
          ...state,
          job: action.payload,
          loading: false
        };
      case LOADING:
        return {
          ...state,
        };
      default:
        return state;
    }
  }
